        this.setState({
            swipper: this.state.swipper + (100 / 6)
        })
    }
    generateItem(i) {
        return <Items img={this.items.imgs[i]} title={this.items.titles[i]} downloads={this.items.downloads[i]} downloadsBtn={this.items.downloadsBtns[i]} stars={this.items.stars[i]}/>
    }

    render() {
        return (
            <div className='featured bg-darkAlt p-3 rounded-5'>
                <div className='d-flex justify-content-between align-items-center'>
                    <MainTitle first={this.mainTitleInfo.first}>featured</MainTitle>
                    <div className='options'>
                        <FaAngleLeft className='text-white pointer fs-4' onClick={this.handleClickLeft}></FaAngleLeft>
                        <FaAngleRight className='text-white pointer fs-4' onClick={this.handleClickRight}></FaAngleRight>
                    </div>
                </div>
                <div className='carowsel overflow-hidden'>
                    <div className='swipper d-flex gap-5 mt-3' style={{translate: this.state.swipper}}>
                        {this.generateItem(0)}
                        {this.generateItem(1)}
                        {this.generateItem(2)}
                        {this.generateItem(0)}
                        {this.generateItem(1)}
                        {this.generateItem(2)}
                    </div>
                </div>
                
            </div>
        )
    }
}

export default Featured;
