import React, { Component } from 'react'
import profileHeader from '../../assets/images/profile-header.jpg'; 

class NavLink extends Component {
    constructor() {
        super();
        this.handleClickLi = this.handleClickLi.bind(this); 
    }
    handleClickLi(e) {
        document.querySelectorAll(".nav li").forEach((li) => {
            li.classList.remove("active");
        });
        e.target.parentNode.classList.add("active");
        e.target.classList.add("active");
    }
    render() {
        return (
            <li onClick={this.handleClickLi} className={this.props.active}><a href={this.props.link}  className='text-secondryy fs-rg1 tr-1'>{this.props.name}</a></li>
        )
    }
}

class NavLinkProfile extends Component {
    render() {
        return (
            <li className='p-2 m-0 d-flex justify-content-center align-items-center tr-1' onClick={this.handleClickLi}>
                <a href={this.props.link}  className='text-secondryy fs-rg1 tr-1 me-3'>{this.props.name}</a>
                <img src={profileHeader} alt='profile' className='rounded-5 w-100 h-100'/>
            </li>
        )
    }
}

export default NavLink;
export {NavLinkProfile};
