import React, { Component } from 'react'
import MainTitle from '../../../container/MainTitle';
import {FaAngleRight, FaAngleLeft} from 'react-icons/fa'
import Img1 from '../../../../assets/images/featured-01.jpg'
import Img2 from '../../../../assets/images/featured-02.jpg'
import Img3 from '../../../../assets/images/featured-03.jpg'
import Items from './Items';

class Featured extends Component {
    constructor() {
        super();
        this.mainTitleInfo = {
            first: "games"
        }
        this.items = {
            imgs: [Img1,Img2,Img3],
            titles: ["CS-GO","Isalnd Rusty","Gamezer"],
            downloads: ["254k","270k","308K"],
            downloadsBtns: [4.2,4.4,4.8],
            stars: [4.1,4.5,4.5]
        }
        this.state = {
            swipper: 0
        }
        this.handleClickLeft = this.handleClickLeft.bind(this); 
        this.handleClickRight = this.handleClickRight.bind(this); 
    }
    handleClickLeft() {
        if((this.state.swipper !== 0) && (this.state.swipper + (document.querySelector(".item").clientWidth) <= 0)) {
            this.setState({
                swipper: this.state.swipper + (document.querySelector(".item").clientWidth)
            })
        } else {
            this.setState({
                swipper: 0
            })
        }
    }
    handleClickRight() {
        if((this.state.swipper !== -780) && (this.state.swipper - (document.querySelector(".item").clientWidth) >= -780)) {
            this.setState({
                swipper: this.state.swipper - (document.querySelector(".item").clientWidth)
            })
        } else {
            this.setState({
                swipper: -780
            })
        }
    }
    generateItem(i) {
        return <Items img={this.items.imgs[i]} title={this.items.titles[i]} downloads={this.items.downloads[i]} downloadsBtn={this.items.downloadsBtns[i]} stars={this.items.stars[i]}/>
    }

    render() {
        return (
            <div className='featured bg-darkAlt p-3 rounded-5'>
                <div className='d-flex justify-content-between align-items-center'>
                    <MainTitle first={this.mainTitleInfo.first}>featured</MainTitle>
                    <div className='options'>
                        <FaAngleLeft className='option text-white pointer fs-4' onClick={this.handleClickLeft}></FaAngleLeft>
                        <FaAngleRight className='option text-white pointer fs-4' onClick={this.handleClickRight}></FaAngleRight>
                    </div>
                </div>
                <div className='carowsel overflow-hidden'>
                    <div className='swipper d-flex gap-5 mt-3 tr-2' style={{translate: this.state.swipper}}>
                        {this.generateItem(0)}
                        {this.generateItem(1)}
                        {this.generateItem(2)}
                        {this.generateItem(0)}
                        {this.generateItem(1)}
                        {this.generateItem(2)}
                    </div>
                </div>
                
            </div>
        )
    }
}

export default Featured;
